/**
 * Class PageNotificationService
 * @class
 */
@Injectable({
    providedIn: 'root'
})
export class PageNotificationService extends NotificationProvider {

    /**
     * successSeverity property
     * @type {string}
     */
    private readonly successSeverity = 'success';

    /**
     * infoSeverity property
     * @type {string}
     */
    private readonly infoSeverity = 'info';

    /**
     * warnSeverity property
     * @type {string}
     */
    private readonly warnSeverity = 'warn';

    /**
     * errorSeverity property
     * @type {string}
     */
    private readonly errorSeverity = 'error';

    /**
     * createMsg property
     * @type {string}
     */
    private readonly createMsg = 'Registro incluído com sucesso!';

    /**
     * updateMsg property
     * @type {string}
     */
    private readonly updateMsg = 'Registro alterado com sucesso!';

    /**
     * deleteMsg property
     * @type {string}
     */
    private readonly deleteMsg = 'Registro excluído com sucesso!';

    /**
     * constructor method
     * @constructor
     * @param {MessageService} messageService
     */
    constructor(private messageService: MessageService) {
        super();
    }

    /**
     * addCreateMsg method
     * @param {string} title
     */
    addCreateMsg(title?: string) {
        this.addSuccessMessage(this.createMsg, title);
    }

    /**
     * addSuccessMessage method
     * @param {string} message
     * @param {string} title
     */
    addSuccessMessage(message: string, title?: string) {
        this.addMsg(this.successSeverity, message, title);
    }

    /**
     * addMsg method
     * @param {string} severity
     * @param {string} msg
     * @param {string} title
     */
    private addMsg(severity: string, msg: string, title?: string) {
        this.messageService.add({
          severity: severity,
          summary: title,
          detail: msg
        });
    }

    /**
     * addUpdateMsg method
     * @param {string} title
     */
    addUpdateMsg(title?: string) {
        this.addSuccessMessage(this.updateMsg, title);
    }

    /**
     * addDeleteMsg method
     * @param {string} title
     */
    addDeleteMsg(title?: string) {
        this.addSuccessMessage(this.deleteMsg, title);
    }

    /**
     * addInfoMessage method
     * @param {string} message
     * @param {string} title
     */
    addInfoMessage(message: string, title?: string) {
        this.addMsg(this.infoSeverity, message, title);
    }

    /**
     * addWarnMessage method
     * @param {string} message
     * @param {string} title
     */
    addWarnMessage(message: string, title?: string) {
        this.addMsg(this.warnSeverity, message, title);
    }

    /**
     * addErrorMessage method
     * @param {string} message
     * @param {string} title
     */
    addErrorMessage(message: string, title?: string) {
        this.addMsg(this.errorSeverity, message, title);
    }

    /**
     * addErrorProblem method
     * @param {ApplicationProblemType} problem
     */
    addErrorProblem(problem: ApplicationProblemType): void {
        this.addMsg(this.errorSeverity, problem.detail, problem.title);
    }

}
import { Component, Input, OnInit, QueryList, Output, EventEmitter } from '@angular/core';
import { PageNotificationService } from './../../page-notification/page-notification.service';

@Component({
    selector: 'basis-show-column',
    templateUrl: './show-column.component.html'
})
export class ShowColumn implements OnInit {

    /**
     * atributo que representa as colunas da tabela
     * @type {QueryList}
     */
    @Input() columns: QueryList<any>;

    /**
     * lista de colunas disponívels na tabela
     * @type {Array}
     */
    options: any[] = [];

    /**
     * Valores do select de colunas
     * @type {Array}
     */
    values: any[] = [];

    /**
     * Último valor válido para o select.
     * @type {Array}
     */
    private lastValue: any[] = [];

    /**
     * Evento para atualizar a visibilidade das colunas da tabela
     * @type {EventEmitter}
     */
    @Output() updateVisibleColumns: EventEmitter<any> = new EventEmitter();

    constructor(private pageNotificationService: PageNotificationService) {}

    ngOnInit () {
        this.columns.forEach((column) => {
            const col = {field: column.field, filterType: column.filterType, header: column.header};
            this.options.push(col);
            this.values.push(col)
            this.lastValue.push(col);
        });
    }

    mostrarColunas(event) {
        if (this.values.length) {
            this.lastValue = event.value;
            this.updateVisibleColumns.emit(this.values);
		} else {
            this.lastValue.map((item) => this.values.push(item));
			this.pageNotificationService.addErrorMessage('Não é possível exibir menos de uma coluna');
        }
    }
}
