export function crudRouteBuilder(route: string, parent: any, list: any, form: any): Route {
    return   {
        path: route,
        component: parent,
        data: { breadcrumb: route },
        children: [
            {
                path: 'list',
                component: list,
                data: { breadcrumb: 'list' },
            },
            {
                path: ':action',
                component: form,
                data: { breadcrumb: ':action' },
            },
            {
                path: ':action/:id',
                component: form,
                resolve: {
                    value: CrudResolveGuard
                },
                data: { breadcrumb: ':action' },
            }
        ]
    }


}
import { CrudService } from "./crud-service.service";
import { Observable } from "rxjs";
import { HttpClient, HttpParams } from "@angular/common/http";

export class CrudServiceNuvem<I, E> implements CrudService<I, E> {

    constructor(public uri: string, protected http: HttpClient) {
    }

    save(entity: E): Observable<E> {
        return this.http.post<E>(this.uri, entity);
    }

    edit(entity: E): Observable<E> {
        return this.http.put<E>(this.uri, entity);
    }

    find(id: I): Observable<E> {
        return this.http.get<E>(`${this.uri}/${id}`);
    }

    delete(id: I): Observable<E> {
        return this.http.delete<E>(`${this.uri}/${id}`);
    }

    findAll(entity: E): Observable<E> {
        let params = new HttpParams();

        for (let field in entity) {
            params = params.append(field, <any> entity[field]);
        }

        return this.http.get<E>(`${this.uri}`, { params: params });
    }

}
import { Injectable } from '@angular/core';
import {
  HttpEvent, HttpInterceptor, HttpHandler, HttpRequest, HTTP_INTERCEPTORS, HttpResponse
} from '@angular/common/http';

import { Observable } from 'rxjs';
import { tap } from 'rxjs/operators';
import { PageNotificationService } from '../page-notification/page-notification.service';

enum STATUS { OK=200, CREATED=201  }

@Injectable({ providedIn: 'root' })
export class SuccessMessageHandlerInterceptor implements HttpInterceptor {

  constructor(private notification: PageNotificationService) {

  }

  intercept(req: HttpRequest<any>, next: HttpHandler): Observable<HttpEvent<any>> {
    return next.handle(req).pipe(
        tap((event) => {
          if (event instanceof HttpResponse) {
            if (event.headers.has("X-nuvem-alert")) {
              this.notification.addSuccessMessage(event.headers.get("X-nuvem-alert"));
            } else if ((event.status === STATUS.OK || event.status === STATUS.CREATED) && req.method != "GET") {
              this.notification.addSuccessMessage("Operação realizada com sucesso!");
            }
          }
        })
    );
  }

}

export const httpInterceptorProviders = [
  { provide: HTTP_INTERCEPTORS, useClass: SuccessMessageHandlerInterceptor, multi: true },
];
